mapper.select:
SELECT last_updates.update_id AS updateId, last_updates.update_type AS updateType, last_updates.update_type AS updateType, last_updates.project_id AS projectId, last_updates.volume_id AS volumeId, last_updates.chapter_id AS chapterId, last_updates.show_time AS showTime, last_updates.description AS description, c.title AS chapterTitle, v.name_title AS volumeTitle, v.name_short AS volumeTitleShort, c.url AS chapterUrl, v.url AS volumeUrl FROM (SELECT * FROM updates WHERE show_time < (now())
AND volume_id = #{volumeId}
AND update_type = #{updateType}
AND project_id = coalesce((SELECT parent_id FROM projects WHERE project_id = #{projectId}), #{projectId})
ORDER BY show_time DESC LIMIT #{limitFrom}, #{limitTo}) last_updates LEFT JOIN chapters c ON last_updates.chapter_id = c.chapter_id LEFT JOIN volumes v ON last_updates.volume_id = v.volume_id
